package Live_Class_5;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, c;
		a=10;
		b=20;
		c=a+b;
		System.out.println("a+b : "+c);
		c+=a;
		System.out.println("c+=a : "+c);
		c-=a;
		System.out.println("c-=a : "+c);
		c/=a;
		System.out.println("c/=a : "+c);
		c%=a;
		System.out.println("c%=a : "+c);
	}

}
